import { useState, useEffect, createContext, useContext } from 'react';
import axios from 'axios';

// Configure axios base URL
axios.defaults.baseURL = 'http://localhost:8000/api';

// Demo users for fallback
const demoUsers = [
    { email: 'john@agent.com', password: 'password123', name: 'John Doe', id: 1 },
    { email: 'sarah@agent.com', password: 'password123', name: 'Sarah Smith', id: 2 }
];

// Demo properties for different users
const demoProperties = {
    1: [ // John Doe's properties
        { id: 1, title: 'Luxury Villa in South Goa', location: 'South Goa', price: 15000, rating: 4.9, isGuestFavorite: true, image: 'https://picsum.photos/400/300?random=1', beds: 3, bedrooms: 2, bathrooms: 2, type: 'villa', ownerId: 1 },
        { id: 2, title: 'Beach House in Canacona', location: 'Canacona', price: 12000, rating: 4.8, isGuestFavorite: true, image: 'https://picsum.photos/400/300?random=2', beds: 2, bedrooms: 1, bathrooms: 1, type: 'beach_house', ownerId: 1 },
        { id: 3, title: 'Modern Apartment in Majorda', location: 'Majorda', price: 8000, rating: 4.7, isGuestFavorite: false, image: 'https://picsum.photos/400/300?random=3', beds: 2, bedrooms: 1, bathrooms: 1, type: 'apartment', ownerId: 1 }
    ],
    2: [ // Sarah Smith's properties
        { id: 4, title: 'Cozy Cottage in Arpora', location: 'Arpora', price: 6000, rating: 4.6, isGuestFavorite: false, image: 'https://picsum.photos/400/300?random=4', beds: 1, bedrooms: 1, bathrooms: 1, type: 'cottage', ownerId: 2 },
        { id: 5, title: 'Penthouse in Canacona', location: 'Canacona', price: 18000, rating: 5.0, isGuestFavorite: true, image: 'https://picsum.photos/400/300?random=5', beds: 4, bedrooms: 3, bathrooms: 2, type: 'penthouse', ownerId: 2 },
        { id: 6, title: 'Studio in Salcete', location: 'Salcete', price: 4000, rating: 4.5, isGuestFavorite: false, image: 'https://picsum.photos/400/300?random=6', beds: 1, bedrooms: 1, bathrooms: 1, type: 'studio', ownerId: 2 }
    ]
};

const AuthContext = createContext();

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [userProperties, setUserProperties] = useState([]);

    useEffect(() => {
        checkAuth();
    }, []);

    const checkAuth = async () => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const response = await axios.get('/auth/user', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUser(response.data.agent);
                // Load user-specific properties
                loadUserProperties(response.data.agent.id);
            } catch (error) {
                console.error('Error fetching user data:', error);
                localStorage.removeItem('token');
            }
        }
        setLoading(false);
    };

    const loadUserProperties = (userId) => {
        // In real app, this would fetch from API
        // For demo, use the demo properties
        const properties = demoProperties[userId] || [];
        setUserProperties(properties);
    };

    const login = async (email, password) => {
        try {
            // Try real API first
            const response = await axios.post('/auth/login', { email, password });
            localStorage.setItem('token', response.data.token);
            setUser(response.data.agent);
            // Load user-specific properties
            loadUserProperties(response.data.agent.id);
            return { success: true, data: response.data };
        } catch (error) {
            console.error('Login error:', error);

            // Fallback to demo authentication if backend is not available
            if (error.code === 'ECONNREFUSED' || error.code === 'ERR_NETWORK') {
                const demoUser = demoUsers.find(u => u.email === email && u.password === password);
                if (demoUser) {
                    // Create a fake token for demo purposes
                    const fakeToken = 'demo-token-' + Date.now();
                    localStorage.setItem('token', fakeToken);
                    setUser(demoUser);
                    // Load user-specific properties
                    loadUserProperties(demoUser.id);
                    return { success: true, data: { agent: demoUser, token: fakeToken } };
                }
                return { success: false, error: 'Invalid email or password' };
            }

            // Handle other API errors
            const errorMessage = error.response?.data?.message || 
                              error.response?.data?.error || 
                              'Login failed. Please try again.';
            return { success: false, error: errorMessage };
        }
    };

    const logout = async () => {
        try {
            const token = localStorage.getItem('token');
            if (token) {
                await axios.post('/auth/logout', {}, {
                    headers: { Authorization: `Bearer ${token}` }
                });
            }
        } catch (error) {
            console.error('Logout error:', error);
        } finally {
            localStorage.removeItem('token');
            setUser(null);
            setUserProperties([]);
        }
    };

    const updatePropertyPrice = async (propertyId, newPrice) => {
        try {
            // Try real API first
            const token = localStorage.getItem('token');
            await axios.put(`/properties/${propertyId}/price`, 
                { price: newPrice },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            // Update local state
            setUserProperties(prev => 
                prev.map(prop => 
                    prop.id === propertyId ? { ...prop, price: newPrice } : prop
                )
            );
            return { success: true };
        } catch (error) {
            console.error('Error updating property price:', error);

            // Fallback to local update for demo
            if (error.code === 'ECONNREFUSED' || error.code === 'ERR_NETWORK') {
                setUserProperties(prev => 
                    prev.map(prop => 
                        prop.id === propertyId ? { ...prop, price: newPrice } : prop
                    )
                );
                return { success: true };
            }
            return { success: false, error: 'Failed to update price' };
        }
    };

    return (
        <AuthContext.Provider value={{ 
            user, 
            login, 
            logout, 
            loading, 
            userProperties,
            updatePropertyPrice
        }}>
            {children}
        </AuthContext.Provider>
    );
};
