import { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from '../Home/SearchBar';
import ExperienceCard from './ExperienceCard';
import CategoryCarousel from './CategoryCarousel';

const ExperiencesPage = () => {
    const [originals, setOriginals] = useState([]);
    const [cityExperiences, setCityExperiences] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchExperiences();
    }, []);

    const fetchExperiences = async () => {
        setLoading(true);
        try {
            // Fetch Airbnb Originals
            const originalsRes = await axios.get('/api/experiences?is_original=true&per_page=16');
            setOriginals(originalsRes.data.data);

            // Fetch popular cities
            const cities = ['Miami', 'Chicago', 'Nashville'];
            const cityPromises = cities.map(city => 
                axios.get(`/api/experiences?city=${city}&per_page=8`)
            );
            const cityResults = await Promise.all(cityPromises);

            const cityData = {};
            cities.forEach((city, index) => {
                cityData[city] = cityResults[index].data.data;
            });
            setCityExperiences(cityData);
        } catch (error) {
            console.error('Error fetching experiences:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="experiences-page">
            {/* Search Bar */}
            <div className="search-bar-wrapper bg-white border-bottom sticky-top">
                <div className="container py-3">
                    <SearchBar 
                        placeholder="Search experiences"
                        showGuestCount={true}
                    />
                </div>
            </div>

            {/* Airbnb Originals Section */}
            <section className="py-5 bg-light">
                <div className="container">
                    <div className="d-flex justify-content-between align-items-center mb-4">
                        <div>
                            <h2 className="fw-bold mb-1">Airbnb Originals</h2>
                            <p className="text-muted mb-0">
                                Hosted by the world's most interesting people
                            </p>
                        </div>
                        <a href="/experiences?is_original=true" className="text-dark fw-semibold text-decoration-none">
                            See all <i className="bi bi-arrow-right"></i>
                        </a>
                    </div>

                    <div className="row g-3 g-lg-4">
                        {originals.slice(0, 8).map(experience => (
                            <div key={experience.id} className="col-12 col-sm-6 col-lg-3">
                                <ExperienceCard experience={experience} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* Popular with travellers Section */}
            {Object.entries(cityExperiences).map(([city, experiences]) => (
                <section key={city} className="py-5">
                    <div className="container">
                        <div className="d-flex justify-content-between align-items-center mb-4">
                            <h3 className="h4 fw-bold">Experiences in {city}</h3>
                            <a href={`/experiences?city=${city}`} className="text-dark fw-semibold text-decoration-none">
                                See all <i className="bi bi-arrow-right"></i>
                            </a>
                        </div>

                        <div className="horizontal-scroll">
                            <div className="d-flex gap-3">
                                {experiences.map(experience => (
                                    <div key={experience.id} style={{minWidth: '300px'}}>
                                        <ExperienceCard experience={experience} />
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </section>
            ))}
        </div>
    );
};

export default ExperiencesPage;
