import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { useAuth } from '../../hooks/useAuth';
import SearchBar from './SearchBar';
import PropertyCard from './PropertyCard';

const HomePage = () => {
    const [filters, setFilters] = useState({
        location: 'Search destinations',
        checkIn: 'Add dates',
        checkOut: 'Add dates',
        guests: 'Add guests'
    });
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const { user, userProperties } = useAuth();

    useEffect(() => {
        setLoading(false);
        setTotalPages(1);
    }, [userProperties]);

    const handleSearch = (searchFilters) => {
        setFilters(searchFilters);
        setPage(1);
    };

    const handleLocationSelect = (location) => {
        setFilters(prev => ({ ...prev, location }));
        setShowSuggestions(false);
    };

    const handlePriceUpdate = (property) => {
        console.log('Update price for:', property);
    };

    return (
    <>
        {/* Search Bar Section */}
        <div className="container py-4">
            <div className="search-bar-wrapper bg-white rounded-4 shadow-lg p-4">
                <div className="row g-3 align-items-end">
                    <div className="col-md-3">
                        <label className="form-label fw-semibold">Where</label>
                        <div 
                            className="border rounded-3 p-3 d-flex align-items-center cursor-pointer"
                            onClick={() => setShowSuggestions(true)}
                        >
                            <i className="bi bi-search me-2 text-muted"></i>
                            <span className="flex-grow-1">{filters.location}</span>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <label className="form-label fw-semibold">When</label>
                        <div className="border rounded-3 p-3 d-flex align-items-center">
                            <i className="bi bi-calendar me-2 text-muted"></i>
                            <span className="flex-grow-1">{filters.checkIn}</span>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <label className="form-label fw-semibold">Who</label>
                        <div className="border rounded-3 p-3 d-flex align-items-center">
                            <i className="bi bi-person me-2 text-muted"></i>
                            <span className="flex-grow-1">{filters.guests}</span>
                        </div>
                    </div>
                </div>
                <div className="row mt-3">
                    <div className="col-12">
                        <button 
                            className="btn btn-danger rounded-pill px-5 py-3"
                            onClick={() => setShowSuggestions(true)}
                        >
                            <i className="bi bi-search me-2"></i>
                            Search
                        </button>
                    </div>
                </div>
            </div>
        </div>

        {/* Main Content */}
        <div className="container py-4">
            {/* Section Title */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h1 className="mb-0 fw-bold">
                    Popular homes in South Goa
                </h1>
                <div className="text-muted">
                    {userProperties.length} of {userProperties.length} items showing
                </div>
            </div>

            {/* Properties Grid */}
            {loading ? (
                <div className="text-center py-5">
                    <div className="spinner-border text-danger" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            ) : (
                <Row className="g-3 g-lg-4">
                    {userProperties.map(property => (
                        <Col key={property.id} md={6} lg={4} xl={3}>
                            <PropertyCard 
                                property={property} 
                                user={user}
                                onPriceUpdate={handlePriceUpdate}
                            />
                        </Col>
                    ))}
                </Row>
            )}

            {/* Available Next Month Section */}
            <div className="mt-5">
                <h2 className="mb-4 fw-bold">Available next month in North Goa</h2>
                <div className="text-muted mb-4">{userProperties.length} of {userProperties.length} items showing</div>

                <Row className="g-3 g-lg-4">
                    {userProperties.slice(0, 7).map(property => (
                        <Col key={`next-${property.id}`} md={6} lg={4} xl={3}>
                            <PropertyCard 
                                property={property} 
                                user={user}
                                onPriceUpdate={handlePriceUpdate}
                            />
                        </Col>
                    ))}
                </Row>
            </div>
        </div>

        {/* Destination Suggestions Modal */}
        {showSuggestions && (
            <div 
                className="modal-backdrop fade-in"
                style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    backgroundColor: 'rgba(0, 0, 0, 0.3)',
                    zIndex: 9999,
                    display: 'flex',
                    alignItems: 'flex-start',
                    justifyContent: 'center',
                    paddingTop: '120px',
                    backdropFilter: 'blur(4px)',
                    WebkitBackdropFilter: 'blur(4px)'
                }}
                onClick={() => setShowSuggestions(false)}
            >
                <div 
                    className="modern-card slide-up-fast"
                    style={{
                        width: '90%',
                        maxWidth: '640px',
                        maxHeight: '70vh',
                        backgroundColor: 'white',
                        borderRadius: '24px',
                        overflow: 'hidden',
                        boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25), 0 0 0 1px rgba(0, 0, 0, 0.04)',
                        display: 'flex',
                        flexDirection: 'column',
                        animation: 'slideUp 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                        transform: 'translateY(0)'
                    }}
                    onClick={(e) => e.stopPropagation()}
                >
                    {/* Header */}
                    <div className="p-4 border-bottom bg-white">
                        <div className="d-flex justify-content-between align-items-center mb-3">
                            <h4 className="mb-0 fw-bold" style={{fontSize: '20px'}}>Search</h4>
                            <button 
                                className="btn btn-sm rounded-circle hover-scale"
                                onClick={() => setShowSuggestions(false)}
                                style={{
                                    width: '36px',
                                    height: '36px',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    backgroundColor: 'var(--gray-100)',
                                    border: 'none',
                                    transition: 'all 0.2s ease'
                                }}
                            >
                                <i className="bi bi-x-lg"></i>
                            </button>
                        </div>

                        {/* Search Input */}
                        <div className="position-relative">
                            <i className="bi bi-search position-absolute" style={{
                                left: '16px',
                                top: '50%',
                                transform: 'translateY(-50%)',
                                color: 'var(--gray-500)',
                                fontSize: '18px'
                            }}></i>
                            <input
                                type="text"
                                className="form-control ps-5"
                                placeholder="Search destinations"
                                autoFocus
                                style={{
                                    fontSize: '16px',
                                    padding: '14px 18px 14px 46px',
                                    borderRadius: '16px',
                                    border: '2px solid var(--gray-200)',
                                    backgroundColor: 'var(--gray-50)'
                                }}
                            />
                        </div>
                    </div>

                    {/* Destinations List */}
                    <div className="flex-grow-1 overflow-auto" style={{ maxHeight: '400px' }}>
                        <div className="p-3">
                            <div className="mb-4">
                                <h6 className="text-muted mb-4" style={{fontSize: '13px', fontWeight: 600, letterSpacing: '0.5px'}}>
                                    SUGGESTED DESTINATIONS
                                </h6>
                            </div>

                            {/* Nearby */}
                            <div
                                className="d-flex align-items-center gap-4 p-4 rounded-4 cursor-pointer hover-lift-fast mb-3"
                                style={{
                                    transition: 'all 0.15s cubic-bezier(0.4, 0, 0.2, 1)',
                                    backgroundColor: 'transparent',
                                    borderRadius: '16px'
                                }}
                                onClick={() => handleLocationSelect('Nearby')}
                                onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = 'var(--gray-50)';
                                    e.currentTarget.style.transform = 'translateX(4px)';
                                }}
                                onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = 'transparent';
                                    e.currentTarget.style.transform = 'translateX(0)';
                                }}
                            >
                                <div 
                                    className="d-flex align-items-center justify-content-center rounded-4"
                                    style={{
                                        width: '56px',
                                        height: '56px',
                                        fontSize: '28px',
                                        backgroundColor: 'var(--gray-100)',
                                        flexShrink: 0
                                    }}
                                >

                                </div>

                                <div className="flex-grow-1">
                                    <div className="fw-semibold text-dark mb-1" style={{fontSize: '16px'}}>
                                        Nearby
                                    </div>
                                    <div className="text-muted" style={{fontSize: '14px'}}>
                                        Find what's around you
                                    </div>
                                </div>

                                <i className="bi bi-chevron-right text-muted" style={{fontSize: '16px'}}></i>
                            </div>

                            {/* North Goa */}
                            <div
                                className="d-flex align-items-center gap-4 p-4 rounded-4 cursor-pointer hover-lift-fast mb-3"
                                style={{
                                    transition: 'all 0.15s cubic-bezier(0.4, 0, 0.2, 1)',
                                    backgroundColor: 'transparent',
                                    borderRadius: '16px'
                                }}
                                onClick={() => handleLocationSelect('North Goa, Goa')}
                                onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = 'var(--gray-50)';
                                    e.currentTarget.style.transform = 'translateX(4px)';
                                }}
                                onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = 'transparent';
                                    e.currentTarget.style.transform = 'translateX(0)';
                                }}
                            >
                                <div 
                                    className="d-flex align-items-center justify-content-center rounded-4"
                                    style={{
                                        width: '56px',
                                        height: '56px',
                                        fontSize: '28px',
                                        backgroundColor: 'var(--gray-100)',
                                        flexShrink: 0
                                    }}
                                >

                                </div>

                                <div className="flex-grow-1">
                                    <div className="fw-semibold text-dark mb-1" style={{fontSize: '16px'}}>
                                        North Goa, Goa
                                    </div>
                                    <div className="text-muted" style={{fontSize: '14px'}}>
                                        Popular beach destination
                                    </div>
                                </div>

                                <i className="bi bi-chevron-right text-muted" style={{fontSize: '16px'}}></i>
                            </div>

                            {/* Puducherry */}
                            <div
                                className="d-flex align-items-center gap-4 p-4 rounded-4 cursor-pointer hover-lift-fast mb-3"
                                style={{
                                    transition: 'all 0.15s cubic-bezier(0.4, 0, 0.2, 1)',
                                    backgroundColor: 'transparent',
                                    borderRadius: '16px'
                                }}
                                onClick={() => handleLocationSelect('Puducherry, Puducherry')}
                                onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = 'var(--gray-50)';
                                    e.currentTarget.style.transform = 'translateX(4px)';
                                }}
                                onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = 'transparent';
                                    e.currentTarget.style.transform = 'translateX(0)';
                                }}
                            >
                                <div 
                                    className="d-flex align-items-center justify-content-center rounded-4"
                                    style={{
                                        width: '56px',
                                        height: '56px',
                                        fontSize: '28px',
                                        backgroundColor: 'var(--gray-100)',
                                        flexShrink: 0
                                    }}
                                >

                                </div>

                                <div className="flex-grow-1">
                                    <div className="fw-semibold text-dark mb-1" style={{fontSize: '16px'}}>
                                        Puducherry, Puducherry
                                    </div>
                                    <div className="text-muted" style={{fontSize: '14px'}}>
                                        For sights like Sri Aurobindo Ashram
                                    </div>
                                </div>

                                <i className="bi bi-chevron-right text-muted" style={{fontSize: '16px'}}></i>
                            </div>

                            {/* Mysore */}
                            <div
                                className="d-flex align-items-center gap-4 p-4 rounded-4 cursor-pointer hover-lift-fast mb-3"
                                style={{
                                    transition: 'all 0.15s cubic-bezier(0.4, 0, 0.2, 1)',
                                    backgroundColor: 'transparent',
                                    borderRadius: '16px'
                                }}
                                onClick={() => handleLocationSelect('Mysore, Karnataka')}
                                onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = 'var(--gray-50)';
                                    e.currentTarget.style.transform = 'translateX(4px)';
                                }}
                                onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = 'transparent';
                                    e.currentTarget.style.transform = 'translateX(0)';
                                }}
                            >
                                <div 
                                    className="d-flex align-items-center justify-content-center rounded-4"
                                    style={{
                                        width: '56px',
                                        height: '56px',
                                        fontSize: '28px',
                                        backgroundColor: 'var(--gray-100)',
                                        flexShrink: 0
                                    }}
                                >

                                </div>

                                <div className="flex-grow-1">
                                    <div className="fw-semibold text-dark mb-1" style={{fontSize: '16px'}}>
                                        Mysore, Karnataka
                                    </div>
                                    <div className="text-muted" style={{fontSize: '14px'}}>
                                        Great for a weekend getaway
                                    </div>
                                </div>

                                <i className="bi bi-chevron-right text-muted" style={{fontSize: '16px'}}></i>
                            </div>

                            {/* Ooty */}
                            <div
                                className="d-flex align-items-center gap-4 p-4 rounded-4 cursor-pointer hover-lift-fast mb-3"
                                style={{
                                    transition: 'all 0.15s cubic-bezier(0.4, 0, 0.2, 1)',
                                    backgroundColor: 'transparent',
