import React from 'react';

const DestinationSuggestions = ({ onSelect }) => {
    const suggestions = [
        {
            icon: 'üß≠',
            title: 'Nearby',
            subtitle: "Find what's around you",
            city: ''
        },
        {
            icon: 'üèñÔ∏è',
            title: 'Puducherry, Puducherry',
            subtitle: 'Great for a weekend getaway',
            city: 'Puducherry'
        },
        {
            icon: '‚õ∞Ô∏è',
            title: 'Kodaikanal, Tamil Nadu',
            subtitle: 'For nature lovers',
            city: 'Kodaikanal'
        },
        {
            icon: 'üå¥',
            title: 'North Goa, Goa',
            subtitle: 'Popular beach destination',
            city: 'Goa'
        }
    ];

    return (
        <div className="destination-suggestions">
            <h6 className="text-muted mb-3" style={{fontSize: '12px', fontWeight: 600}}>
                Suggested destinations
            </h6>
            <div className="list-group list-group-flush">
                {suggestions.map((item, index) => (
                    <button
                        key={index}
                        className="list-group-item list-group-item-action border-0 px-0 py-3 d-flex align-items-center"
                        onClick={() => onSelect(item.city)}
                    >
                        <div 
                            className="d-flex align-items-center justify-content-center rounded me-3"
                            style={{
                                width: '48px',
                                height: '48px',
                                backgroundColor: '#F7F7F7',
                                fontSize: '24px'
                            }}
                        >
                            {item.icon}
                        </div>
                        <div className="flex-grow-1">
                            <div className="fw-semibold">{item.title}</div>
                            <div className="text-muted small">{item.subtitle}</div>
                        </div>
                    </button>
                ))}
            </div>
        </div>
    );
};

export default DestinationSuggestions;
