import React, { useState } from 'react';
import { Card, Badge } from 'react-bootstrap';

const PropertyCard = ({ property, onPriceUpdate, user }) => {
    const [isFavorite, setIsFavorite] = useState(false);

    const handleCardClick = () => {
        // Navigate to property details or expand view
        console.log('Property clicked:', property);
    };

    return (
        <div className="property-card mb-4">
            {/* Image Container */}
            <div className="position-relative" style={{paddingBottom: '100%', overflow: 'hidden', borderRadius: '12px'}}>
                <img 
                    src={property.primary_image || '/placeholder.jpg'} 
                    className="position-absolute w-100 h-100"
                    style={{objectFit: 'cover', top: 0, left: 0}}
                    alt={property.title}
                />

                {/* Guest Favorite Badge */}
                {property.rating >= 4.8 && (
                    <div className="position-absolute top-0 start-0 m-3">
                        <span className="badge bg-white text-dark shadow-sm px-3 py-2">
                            Guest favourite
                        </span>
                    </div>
                )}

                {/* Heart Icon */}
                <button 
                    className="btn position-absolute top-0 end-0 m-3"
                    onClick={(e) => {
                        e.stopPropagation();
                        setIsFavorite(!isFavorite);
                    }}
                    style={{background: 'none', border: 'none'}}
                >
                    <i className={`bi bi-heart${isFavorite ? '-fill' : ''} fs-5`}
                       style={{color: isFavorite ? '#FF385C' : 'rgba(0,0,0,0.5)', 
                              WebkitTextStroke: isFavorite ? 'none' : '2px white'}}></i>
                </button>

                {/* Agent Price Badge */}
                {property.price_type === 'agent' && (
                    <div className="position-absolute bottom-0 end-0 m-3">
                        <Badge bg="airbnb-pink" className="px-2 py-1">
                            <small>Agent Price</small>
                        </Badge>
                    </div>
                )}
            </div>

            {/* Card Content */}
            <div className="mt-3">
                {/* Title & Rating */}
                <div className="d-flex justify-content-between align-items-start mb-1">
                    <h6 className="mb-0 fw-semibold text-dark" style={{fontSize: '15px'}}>
                        {property.title}
                    </h6>
                    {property.rating && (
                        <div className="d-flex align-items-center gap-1">
                            <i className="bi bi-star-fill" style={{fontSize: '12px'}}></i>
                            <span style={{fontSize: '14px'}}>{property.rating}</span>
                        </div>
                    )}
                </div>

                {/* City & Type */}
                <div className="text-muted mb-2" style={{fontSize: '14px'}}>
                    <i className="bi bi-geo-alt me-2"></i>
                    {property.city} · {property.property_type}
                </div>

                {/* Price */}
                <div className="mb-2">
                    <span className="fw-semibold text-dark">
                        ₹{property.price.toLocaleString('en-IN')}
                    </span>
                    <span className="text-muted" style={{fontSize: '14px'}}> for 2 nights</span>
                </div>

                {/* Agent Info & Actions */}
                <div className="d-flex justify-content-between align-items-center">
                    <div className="text-muted small">
                        {property.total_images || 0} photos
                    </div>

                    {property.agent_info && (
                        <div className="text-muted small me-3">
                            Listed by: {property.agent_info.name}
                        </div>
                    )}

                    {user && (
                        <button 
                            className="btn btn-outline-dark btn-sm rounded-pill"
                            onClick={(e) => {
                                e.stopPropagation();
                                onPriceUpdate(property);
                            }}
                        >
                            {property.price_type === 'agent' ? 'Update Price' : 'Set Price'}
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PropertyCard;
