import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { useAuth } from '../../hooks/useAuth';
import SearchBar from './SearchBar';
import PropertyCard from './PropertyCard';

const HomePage = () => {
    const [properties, setProperties] = useState([]);
    const [filters, setFilters] = useState({
        location: 'Search destinations',
        checkIn: 'Add dates',
        checkOut: 'Add dates',
        guests: 'Add guests'
    });
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const { user } = useAuth();

    // Sample property data matching your requirements
    const sampleProperties = [
        {
            id: 1,
            title: 'Flat in South Goa',
            location: 'South Goa',
            price: 6106,
            rating: 5.0,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=1',
            beds: 2,
            bedrooms: 1,
            bathrooms: 1,
            type: 'flat'
        },
        {
            id: 2,
            title: 'Guest suite in Canacona',
            location: 'Canacona',
            price: 4106,
            rating: 4.95,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=2',
            beds: 2,
            bedrooms: 1,
            bathrooms: 1,
            type: 'guest_suite'
        },
        {
            id: 3,
            title: 'Guest house in Majorda',
            location: 'Majorda',
            price: 6848,
            rating: 5.0,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=3',
            beds: 3,
            bedrooms: 2,
            bathrooms: 2,
            type: 'guest_house'
        },
        {
            id: 4,
            title: 'Guest house in Majorda',
            location: 'Majorda',
            price: 10271,
            rating: 4.86,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=4',
            beds: 4,
            bedrooms: 2,
            bathrooms: 2,
            type: 'guest_house'
        },
        {
            id: 5,
            title: 'Flat in Arpora',
            location: 'Arpora',
            price: 5194,
            rating: 5.0,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=5',
            beds: 2,
            bedrooms: 1,
            bathrooms: 1,
            type: 'flat'
        },
        {
            id: 6,
            title: 'Apartment in Canacona',
            location: 'Canacona',
            price: 5820,
            rating: 4.96,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=6',
            beds: 3,
            bedrooms: 2,
            bathrooms: 2,
            type: 'apartment'
        },
        {
            id: 7,
            title: 'Guest house in Majorda',
            location: 'Majorda',
            price: 3732,
            rating: 4.87,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=7',
            beds: 2,
            bedrooms: 1,
            bathrooms: 1,
            type: 'guest_house'
        },
        {
            id: 8,
            title: 'Room in Salcete',
            location: 'Salcete',
            price: 3424,
            rating: 4.8,
            isGuestFavorite: true,
            image: 'https://picsum.photos/400/300?random=8',
            beds: 1,
            bedrooms: 1,
            bathrooms: 1,
            type: 'room'
        }
    ];

    useEffect(() => {
        fetchProperties();
    }, [filters, page]);

    const fetchProperties = async () => {
        setLoading(true);
        try {
            // Simulate API call with sample data
            setTimeout(() => {
                setProperties(sampleProperties);
                setTotalPages(1);
                setLoading(false);
            }, 1000);
        } catch (error) {
            console.error('Error fetching properties:', error);
            setLoading(false);
        }
    };

    const handleSearch = (searchFilters) => {
        setFilters(searchFilters);
        setPage(1);
    };

    const handleLocationSelect = (location) => {
        setFilters(prev => ({ ...prev, location }));
        setShowSuggestions(false);
    };

    const handlePriceUpdate = (property) => {
        console.log('Update price for:', property);
    };

    return (
        <>
            {/* Search Bar Section */}
            <div className="container py-4">
                <div className="search-bar-wrapper bg-white rounded-4 shadow-lg p-4">
                    <div className="row g-3 align-items-end">
                        <div className="col-md-3">
                            <label className="form-label fw-semibold">Where</label>
                            <div 
                                className="border rounded-3 p-3 d-flex align-items-center cursor-pointer"
                                onClick={() => setShowSuggestions(true)}
                            >
                                <i className="bi bi-search me-2 text-muted"></i>
                                <span className="flex-grow-1">{filters.location}</span>
                            </div>
                        </div>
                        <div className="col-md-3">
                            <label className="form-label fw-semibold">When</label>
                            <div className="border rounded-3 p-3 d-flex align-items-center">
                                <i className="bi bi-calendar me-2 text-muted"></i>
                                <span className="flex-grow-1">{filters.checkIn}</span>
                            </div>
                        </div>
                        <div className="col-md-3">
                            <label className="form-label fw-semibold">Who</label>
                            <div className="border rounded-3 p-3 d-flex align-items-center">
                                <i className="bi bi-person me-2 text-muted"></i>
                                <span className="flex-grow-1">{filters.guests}</span>
                            </div>
                        </div>
                    </div>
                    <div className="row mt-3">
                        <div className="col-12">
                            <button 
                                className="btn btn-danger rounded-pill px-5 py-3"
                                onClick={() => setShowSuggestions(true)}
                            >
                                <i className="bi bi-search me-2"></i>
                                Search
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Main Content */}
            <div className="container py-4">
                {/* Section Title */}
                <div className="d-flex justify-content-between align-items-center mb-4">
                    <h1 className="mb-0 fw-bold">
                        Popular homes in South Goa
                    </h1>
                    <div className="text-muted">
                        7 of 8 items showing
                    </div>
                </div>

                {/* Properties Grid */}
                {loading ? (
                    <div className="text-center py-5">
                        <div className="spinner-border text-danger" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (
                    <Row className="g-3 g-lg-4">
                        {properties.map(property => (
                            <Col key={property.id} md={6} lg={4} xl={3}>
                                <PropertyCard 
                                    property={property} 
                                    user={user}
                                    onPriceUpdate={handlePriceUpdate}
                                />
                            </Col>
                        ))}
                    </Row>
                )}

                {/* Available Next Month Section */}
                <div className="mt-5">
                    <h2 className="mb-4 fw-bold">Available next month in North Goa</h2>
                    <div className="text-muted mb-4">7 of 8 items showing</div>

                    <Row className="g-3 g-lg-4">
                        {sampleProperties.slice(0, 7).map(property => (
                            <Col key={`next-${property.id}`} md={6} lg={4} xl={3}>
                                <PropertyCard 
                                    property={property} 
                                    user={user}
                                    onPriceUpdate={handlePriceUpdate}
                                />
                            </Col>
                        ))}
                    </Row>
                </div>
            </div>

            {/* Destination Suggestions Modal */}
            {showSuggestions && (
                <div 
                    className="modal-backdrop fade-in"
                    style={{
                        position: 'fixed',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        backgroundColor: 'rgba(0, 0, 0, 0.3)',
                        zIndex: 9999,
                        display: 'flex',
                        alignItems: 'flex-start',
                        justifyContent: 'center',
                        paddingTop: '120px',
                        backdropFilter: 'blur(4px)',
                        WebkitBackdropFilter: 'blur(4px)'
                    }}
                    onClick={() => setShowSuggestions(false)}
                >
                    <div 
                        className="modern-card slide-up-fast"
                        style={{
                            width: '90%',
                            maxWidth: '640px',
                            maxHeight: '70vh',
                            backgroundColor: 'white',
                            borderRadius: '24px',
                            overflow: 'hidden',
                            boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25), 0 0 0 1px rgba(0, 0, 0, 0.04)',
                            display: 'flex',
                            flexDirection: 'column',
                            animation: 'slideUp 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                            transform: 'translateY(0)'
                        }}
                        onClick={(e) => e.stopPropagation()}
                    >
                        {/* Header */}
                        <div className="p-4 border-bottom bg-white">
                            <div className="d-flex justify-content-between align-items-center mb-3">
                                <h4 className="mb-0 fw-bold" style={{fontSize: '20px'}}>Search</h4>
                                <button 
                                    className="btn btn-sm rounded-circle hover-scale"
                                    onClick={() => setShowSuggestions(false)}
                                    style={{
                                        width: '36px',
                                        height: '36px',
                                        display: 'flex',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        backgroundColor: 'var(--gray-100)',
                                        border: 'none',
                                        transition: 'all 0.2s ease'
                                    }}
                                    >
                                    <i className="bi bi-x-lg"></i>
                                </button>
                            </div>

                            {/* Search Input */}
                            <div className="position-relative">
                                <i className="bi bi-search position-absolute" style={{
                                    left: '16px',
                                    top: '50%',
                                    transform: 'translateY(-50%)',
                                    color: 'var(--gray-500)',
                                    fontSize: '18px'
                                }}></i>
                                <input
                                    type="text"
                                    className="form-control ps-5"
                                    placeholder="Search destinations"
                                    autoFocus
                                    style={{
                                        fontSize: '16px',
                                        padding: '14px 18px 14px 46px',
                                        borderRadius: '16px',
                                        border: '2px solid var(--gray-200)',
                                        backgroundColor: 'var(--gray-50)',
                                        transition: 'all 0.2s ease'
                                    }}
                                />
                            </div>
                        </div>

                        {/* Destinations List */}
                        <div className="flex-grow-1 overflow-auto" style={{ maxHeight: '400px' }}>
                            <div className="p-3">
