import React from 'react';

const ExperienceCard = ({ experience }) => {
    const formatPrice = (price, pricingType) => {
        if (pricingType === 'per_group') {
            return `From ₹${price.toLocaleString('en-IN')} / group`;
        }
        return `From ₹${price.toLocaleString('en-IN')} / guest`;
    };

    return (
        <div className="experience-card">
            {/* Image */}
            <div className="position-relative" style={{paddingBottom: '66.67%', overflow: 'hidden', borderRadius: '12px'}}>
                <img 
                    src={experience.primary_image} 
                    className="position-absolute w-100 h-100"
                    style={{objectFit: 'cover'}}
                    alt={experience.title}
                />

                {/* Original Badge */}
                {experience.is_original && (
                    <div className="position-absolute top-0 start-0 m-3">
                        <span className="badge bg-white text-dark shadow-sm px-3 py-2">
                            Original
                        </span>
                    </div>
                )}

                {/* Popular Badge */}
                {experience.rating >= 4.9 && !experience.is_original && (
                    <div className="position-absolute top-0 start-0 m-3">
                        <span className="badge bg-white text-dark shadow-sm px-3 py-2">
                            Popular
                        </span>
                    </div>
                )}
            </div>

            {/* Content */}
            <div className="mt-3">
                <h6 className="mb-1 fw-normal" style={{fontSize: '14px'}}>
                    {experience.title}
                </h6>

                <div className="d-flex align-items-center gap-2 mb-2">
                    <span className="text-dark fw-semibold" style={{fontSize: '15px'}}>
                        {formatPrice(
                            experience.price_per_guest || experience.group_price,
                            experience.pricing_type
                        )}
                    </span>
                    {experience.rating && (
                        <>
                            <span className="text-muted">·</span>
                            <div className="d-flex align-items-center gap-1">
                                <i className="bi bi-star-fill" style={{fontSize: '11px'}}></i>
                                <span style={{fontSize: '14px'}}>{experience.rating}</span>
                            </div>
                        </>
                    )}
                </div>

                <div className="text-muted small mb-1">
                    {experience.duration_hours} hours · {experience.max_guests} guests
                </div>
            </div>
        </div>
    );
};

export default ExperienceCard;
