import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';

const Navbar = () => {
    const [isScrolled, setIsScrolled] = useState(false);
    const { user, logout } = useAuth();

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 0);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <nav className={`navbar navbar-expand-lg bg-white sticky-top ${isScrolled ? 'shadow-sm' : ''}`}>
            <div className="container-custom px-4 px-lg-5 d-flex align-items-center justify-content-between">
                {/* Logo */}
                <a className="navbar-brand d-flex align-items-center" href="/">
                    <svg width="30" height="32" viewBox="0 0 30 32" fill="#FF385C">
                        {/* <path d="M29.24 22.68c-.16-.39-.31-.8-.31-1.24 0-1.36.92-2.45 2.45-2.45 1.38 0 2.5 1.12 2.5 2.5 0 .69-.28 1.32-.73 1.79-.18.2-.46.28-.73.28-1.11 0-.63-.39-1.17-.73-1.64-.73-.88 0-1.63-.29-2.42-.87-3.22-1.27-1.43-2.09-2.81-2.09-1.88 0-3.47.96-4.43 2.24-.96.96-2.02 1.49-2.02 1.01 0 1.93.41 2.89 1.23 2.89z"/> */}
                    </svg>
                    <span className="ms-2 d-none d-md-inline">airbnb</span>
                </a>

                {/* Center Navigation - Desktop */}
                <div className="d-none d-lg-flex mx-auto">
                    <ul className="nav">
                        <li className="nav-item"><a className="nav-link active fw-semibold" href="/">Homes</a></li>
                        <li className="nav-item"><a className="nav-link text-muted" href="/experiences">Experiences <span className="badge bg-info ms-1">NEW</span></a></li>
                        <li className="nav-item"><a className="nav-link text-muted" href="/services">Services</a></li>
                    </ul>
                </div>

                {/* Right Side */}
                <div className="ms-auto">
                    <div className="d-flex align-items-center gap-3">
                        {user ? (
                            <>
                                <a href="/dashboard" className="btn btn-link text-dark">Dashboard</a>
                                <button onClick={logout} className="btn btn-outline-dark rounded-pill">Logout</button>
                            </>
                        ) : (
                            <a href="/login" className="btn btn-outline-dark rounded-pill ms-auto">Agent Login</a>
                        )}
                        <button className="btn btn-link text-dark d-none d-md-block ms-auto"><i className="bi bi-globe"></i></button>
                        <button className="btn border rounded-pill d-flex align-items-center px-3 py-2 ms-auto"><i className="bi bi-list fs-5"></i><i className="bi bi-person-circle fs-5 ms-2"></i></button>
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
