import React, { useState } from 'react';

const ExperienceCard = ({ experience }) => {
    const [isFavorite, setIsFavorite] = useState(false);
    const [isHovered, setIsHovered] = useState(false);

    const formatPrice = (price, pricingType) => {
        if (pricingType === 'per_group') {
            return `From ₹${price.toLocaleString('en-IN')} / group`;
        }
        return `From ₹${price.toLocaleString('en-IN')} / guest`;
    };

    const handleFavoriteClick = () => {
        setIsFavorite(!isFavorite);
    };

    return (
        <div className={`modern-card experience-card ${isHovered ? 'hovered' : ''}`}>
            {/* Image Container with Enhanced Effects */}
            <div 
                className="position-relative overflow-hidden"
                style={{ paddingBottom: '66.67%', borderRadius: '16px' }}
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
            >
                <img 
                    src={experience.primary_image || 'https://picsum.photos/400/300?random=' + experience.id} 
                    className="position-absolute w-100 h-100"
                    style={{ 
                        objectFit: 'cover',
                        transition: 'transform 0.6s cubic-bezier(0.4, 0, 0.2, 1)'
                    }}
                    alt={experience.title}
                />

                {/* Enhanced Badges */}
                <div className="position-absolute top-0 start-0 m-3 d-flex gap-2">
                    {experience.is_original && (
                        <span className="badge badge-original fade-in">
                            <i className="bi bi-patch-check me-1"></i>
                            Original
                        </span>
                    )}

                    {experience.rating >= 4.9 && !experience.is_original && (
                        <span className="badge badge-popular bounce">
                            <i className="bi bi-star-fill me-1"></i>
                            Popular
                        </span>
                    )}

                    {experience.rating >= 4.95 && (
                        <span className="badge badge-agent pulse">
                            <i className="bi bi-award me-1"></i>
                            Guest Favorite
                        </span>
                    )}
                </div>

                {/* Enhanced Heart Icon */}
                <button 
                    className="btn position-absolute top-0 end-0 m-3"
                    onClick={handleFavoriteClick}
                    style={{ 
                        background: 'none', 
                        border: 'none',
                        padding: '8px'
                    }}
                >
                    <i 
                        className={`bi bi-heart${isFavorite ? '-fill' : ''} fs-5`}
                        style={{ 
                            color: isFavorite ? 'var(--airbnb-pink)' : 'rgba(0,0,0,0.5)', 
                            WebkitTextStroke: isFavorite ? 'none' : '2px white',
                            transition: 'all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55)',
                            transform: isFavorite ? 'scale(1.2)' : 'scale(1)'
                        }}
                    ></i>
                </button>
            </div>

            {/* Enhanced Content */}
            <div className="mt-4 p-3">
                <div className="d-flex justify-content-between align-items-start mb-3">
                    <div className="flex-grow-1">
                        <h6 className="mb-2 fw-normal text-gradient" style={{ fontSize: '16px' }}>
                            {experience.title}
                        </h6>

                        <div className="text-muted small mb-2">
                            <i className="bi bi-geo-alt me-1"></i>
                            {experience.city}, {experience.country}
                        </div>
                    </div>

                    <div className="text-end">
                        <div className="d-flex align-items-center gap-2 mb-2">
                            <span className="text-dark fw-semibold" style={{ fontSize: '18px' }}>
                                {formatPrice(
                                    experience.price_per_guest || experience.group_price,
                                    experience.pricing_type
                                )}
                            </span>
                            {experience.rating && (
                                <div className="d-flex align-items-center gap-1">
                                    <i className="bi bi-star-fill" style={{ 
                                        fontSize: '14px', 
                                        color: 'var(--warning-yellow)',
                                        filter: 'drop-shadow(0 0 2px var(--warning-yellow))'
                                    }}></i>
                                    <span className="rating-number" style={{ fontSize: '16px' }}>
                                        {experience.rating}
                                    </span>
                                </div>
                            )}
                        </div>

                        <div className="text-muted small">
                            {experience.duration_hours}h · {experience.max_guests} guests
                        </div>
                    </div>
                </div>

                {/* Enhanced Agent Info */}
                {experience.price_type === 'agent' && (
                    <div className="mt-3 p-2 bg-gradient rounded-3">
                        <div className="d-flex align-items-center gap-2">
                            <i className="bi bi-person-check text-white"></i>
                            <span className="text-white fw-semibold">
                                Agent Price Applied
                            </span>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ExperienceCard;
