import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { API_BASE_URL } from '../../App';
import { useAuth } from '../../hooks/useAuth';
import Navbar from '../Common/Navbar';
import SearchBar from './SearchBar';
import PropertyCard from './PropertyCard';
import CategoryTabs from './CategoryTabs';

const HomePage = () => {
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filters, setFilters] = useState({
        location: '',
        city: '',
        property_type: '',
        agent_id: ''
    });
    const { user } = useAuth();

    useEffect(() => {
        fetchProperties();
    }, [filters]);

    const fetchProperties = async () => {
        setLoading(true);
        try {
            const queryParams = new URLSearchParams();
            Object.entries(filters).forEach(([key, value]) => {
                if (value) queryParams.append(key, value);
            });

            const response = await fetch(`${API_BASE_URL}/properties?${queryParams}`);
            const data = await response.json();

            if (data.success) {
                setProperties(data.data || []);
            }
        } catch (error) {
            console.error('Error fetching properties:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (searchFilters) => {
        setFilters(searchFilters);
    };

    const handlePriceUpdate = (property) => {
        // Open price modal or navigate to price update
        console.log('Update price for:', property);
    };

    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
                <div className="spinner-border" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
            </div>
        );
    }

    return (
        <>
            <Navbar />
            <Container fluid className="container-custom">
                {/* Hero Section with Search */}
                <div className="text-center py-5">
                    <h1 className="display-4 fw-bold mb-4">
                        Find your next stay
                    </h1>
                    <p className="lead text-muted mb-4">
                        Search from over 1,000+ properties across India
                    </p>
                    <SearchBar onSearch={handleSearch} />
                </div>

                {/* Category Tabs */}
                <CategoryTabs />

                {/* Properties Grid */}
                <div className="py-5">
                    <h2 className="mb-4">Available Properties</h2>

                    <Row className="property-grid">
                        {properties.map(property => (
                            <Col key={property.id} className="mb-4">
                                <PropertyCard 
                                    property={property} 
                                    user={user}
                                    onPriceUpdate={handlePriceUpdate}
                                />
                            </Col>
                        ))}
                    </Row>

                    {properties.length === 0 && !loading && (
                        <div className="text-center py-5">
                            <h3>No properties found</h3>
                            <p className="text-muted">
                                Try adjusting your search filters
                            </p>
                            <Button 
                                variant="outline-dark"
                                onClick={() => setFilters({ location: '', city: '', property_type: '', agent_id: '' })}
                            >
                                Clear Filters
                            </Button>
                        </div>
                    )}
                </div>
            </Container>
        </>
    );
};

export default HomePage;
