import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { useAuth } from '../../hooks/useAuth';
import Navbar from '../Common/Navbar';
import SearchBar from './SearchBar';
import PropertyCard from './PropertyCard';
import CategoryTabs from './CategoryTabs';
import DestinationSuggestions from './DestinationSuggestions';

const HomePage = () => {
    const [properties, setProperties] = useState([]);
    const [filters, setFilters] = useState({
        city: '',
        property_type: '',
        agent_id: ''
    });
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const { user } = useAuth();

    useEffect(() => {
        fetchProperties();
    }, [filters, page]);

    const fetchProperties = async () => {
        setLoading(true);
        try {
            const params = new URLSearchParams();
            Object.entries(filters).forEach(([key, value]) => {
                if (value) params.append(key, value);
            });
            params.append('page', page);
            params.append('per_page', 12);

            const response = await fetch(`http://localhost:8000/api/properties?${params}`);
            const data = await response.json();

            if (data.success) {
                setProperties(data.data || []);
                setTotalPages(data.meta.last_page);
            }
        } catch (error) {
            console.error('Error fetching properties:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (searchFilters) => {
        setFilters(searchFilters);
        setPage(1);
    };

    const handleCitySelect = (city) => {
        setFilters(prev => ({ ...prev, city }));
        setPage(1);
    };

    const handlePriceUpdate = (property) => {
        // Open price modal or navigate to price update
        console.log('Update price for:', property);
    };

    return (
        <>
            <Navbar />
            {/* Search Bar - Sticky on scroll */}
            <div className="search-bar-wrapper bg-white border-bottom sticky-top" 
                 style={{top: '80px', zIndex: 999}}>
                <div className="container py-3">
                    <SearchBar onSearch={handleSearch} />
                </div>
            </div>

            {/* Main Content */}
            <div className="container py-4 py-lg-5">
                {/* Section Title */}
                <div className="d-flex justify-content-between align-items-center mb-4">
                    <h2 className="mb-0 fw-semibold">
                        {filters.city 
                            ? `Popular homes in ${filters.city}` 
                            : 'Popular homes in Bengaluru'}
                    </h2>
                    <a href="#" className="text-dark fw-semibold text-decoration-none">
                        <i className="bi bi-arrow-right"></i>
                    </a>
                </div>

                {/* Category Tabs */}
                <CategoryTabs />

                {/* Destination Suggestions */}
                <DestinationSuggestions onSelect={handleCitySelect} />

                {/* Loading State */}
                {loading && (
                    <div className="text-center py-5">
                        <div className="spinner-border text-danger" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                )}

                {/* Properties Grid */}
                {!loading && (
                    <Row className="property-grid g-3 g-lg-4">
                        {properties.map(property => (
                            <Col key={property.id} className="mb-4">
                                <PropertyCard 
                                    property={property} 
                                    user={user}
                                    onPriceUpdate={handlePriceUpdate}
                                />
                            </Col>
                        ))}
                    </Row>
                )}

                {/* No Results */}
                {!loading && properties.length === 0 && (
                    <div className="text-center py-5">
                        <h4 className="text-muted">No properties found</h4>
                        <p className="text-muted">
                            Try adjusting your search filters
                        </p>
                        <button 
                            className="btn btn-outline-dark rounded-pill mt-3"
                            onClick={() => setFilters({ city: '', property_type: '', agent_id: '' })}
                        >
                            Clear Filters
                        </button>
                    </div>
                )}

                {/* Pagination */}
                {totalPages > 1 && !loading && (
                    <nav className="mt-5">
                        <ul className="pagination justify-content-center">
                            <li className={`page-item ${page === 1 ? 'disabled' : ''}`}>
                                <button 
                                    className="page-link rounded-circle me-2"
                                    onClick={() => setPage(p => Math.max(1, p - 1))}
                                    disabled={page === 1}
                                >
                                    <i className="bi bi-chevron-left"></i>
                                </button>
                            </li>

                            {[...Array(totalPages)].map((_, i) => (
                                <li key={i} className={`page-item ${page === i + 1 ? 'active' : ''}`}>
                                    <button 
                                        className="page-link rounded-circle mx-1"
                                        onClick={() => setPage(i + 1)}
                                    >
                                        {i + 1}
                                    </button>
                                </li>
                            ))}

                            <li className={`page-item ${page === totalPages ? 'disabled' : ''}`}>
                                <button 
                                    className="page-link rounded-circle ms-2"
                                    onClick={() => setPage(p => Math.min(totalPages, p + 1))}
                                    disabled={page === totalPages}
                                >
                                    <i className="bi bi-chevron-right"></i>
                                </button>
                            </li>
                        </ul>
                    </nav>
                )}
            </div>
        </>
    );
};

export default HomePage;
