import React from 'react';

const ServiceCard = ({ service }) => {
    const formatPrice = (price, pricingType) => {
        return `From ₹${price.toLocaleString('en-IN')} / guest`;
    };

    return (
        <div className="service-card">
            {/* Image */}
            <div className="position-relative" style={{paddingBottom: '100%', overflow: 'hidden', borderRadius: '12px'}}>
                <img 
                    src={service.primary_image} 
                    className="position-absolute w-100 h-100"
                    style={{objectFit: 'cover'}}
                    alt={service.title}
                />
            </div>

            {/* Content */}
            <div className="mt-3">
                <h6 className="mb-1 fw-normal" style={{fontSize: '14px'}}>
                    {service.title}
                </h6>

                <div className="d-flex align-items-center gap-2 mb-2">
                    <span className="text-dark fw-semibold" style={{fontSize: '15px'}}>
                        {formatPrice(service.price_per_guest, service.pricing_type)}
                    </span>
                    {service.rating && (
                        <>
                            <span className="text-muted">·</span>
                            <div className="d-flex align-items-center gap-1">
                                <i className="bi bi-star-fill" style={{fontSize: '11px'}}></i>
                                <span style={{fontSize: '14px'}}>{service.rating}</span>
                            </div>
                        </>
                    )}
                </div>

                <div className="text-muted small mb-1">
                    {service.duration_minutes} minutes
                </div>

                {/* Min Booking */}
                {service.min_booking_amount && (
                    <div className="text-muted small mb-1">
                        Minimum ₹{service.min_booking_amount.toLocaleString('en-IN')} to book
                    </div>
                )}

                {/* Agent Price Badge */}
                {service.price_type === 'agent' && (
                    <span className="badge bg-info small mt-2">Agent Price</span>
                )}
            </div>
        </div>
    );
};

export default ServiceCard;
