import React from 'react';
import { Card, Badge } from 'react-bootstrap';

const PropertyCard = ({ property, onPriceUpdate, user }) => {
    const handleCardClick = () => {
        // Navigate to property details or expand view
        console.log('Property clicked:', property);
    };

    return (
        <Card className="property-card h-100" onClick={handleCardClick}>
            <div className="position-relative">
                {property.primary_image ? (
                    <div 
                        className="property-image"
                        style={{ 
                            backgroundImage: `url(${property.primary_image})`,
                            backgroundSize: 'cover',
                            backgroundPosition: 'center'
                        }}
                    />
                ) : (
                    <div className="property-image bg-light d-flex align-items-center justify-content-center">
                        <span className="text-muted">No Image</span>
                    </div>
                )}

                {/* Price Badge */}
                <div className="position-absolute top-0 end-0 m-3">
                    <Badge bg="white" text="dark" className="px-3 py-2 shadow-sm">
                        <span className="property-price">${property.price}</span>
                        <span className="text-muted ms-1">/night</span>
                    </Badge>
                </div>

                {/* Agent Badge */}
                {property.agent_info && (
                    <div className="position-absolute top-0 start-0 m-3">
                        <Badge bg="airbnb-pink" className="px-2 py-1">
                            <small>{property.agent_info.name}</small>
                        </Badge>
                    </div>
                )}

                {/* Price Type Indicator */}
                <div className="position-absolute bottom-0 start-0 m-3">
                    <Badge 
                        bg={property.price_type === 'agent' ? 'success' : 'primary'} 
                        className="px-2 py-1"
                    >
                        <small>
                            {property.price_type === 'agent' ? 'Agent Price' : 'Base Price'}
                        </small>
                    </Badge>
                </div>
            </div>

            <Card.Body className="p-3">
                <Card.Title className="property-title">
                    {property.title}
                </Card.Title>

                <Card.Text className="property-location">
                    <i className="bi bi-geo-alt me-2"></i>
                    {property.city} Â· {property.property_type}
                </Card.Text>

                <div className="d-flex justify-content-between align-items-center mt-3">
                    <div className="text-muted small">
                        {property.total_images || 0} photos
                    </div>

                    {user && (
                        <button 
                            className="btn btn-outline-dark btn-sm rounded-pill"
                            onClick={(e) => {
                                e.stopPropagation();
                                onPriceUpdate(property);
                            }}
                        >
                            {property.price_type === 'agent' ? 'Update Price' : 'Set Price'}
                        </button>
                    )}
                </div>
            </Card.Body>
        </Card>
    );
};

export default PropertyCard;
