import { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from '../Home/SearchBar';
import ServiceCard from './ServiceCard';
import ServiceTypeGrid from './ServiceTypeGrid';

const ServicesPage = () => {
    const [serviceTypes, setServiceTypes] = useState([]);
    const [services, setServices] = useState([]);
    const [categorizedServices, setCategorizedServices] = useState({});
    const [loading, setLoading] = useState(true);
    const [userCity, setUserCity] = useState('Columbus');

    useEffect(() => {
        fetchServices();
    }, []);

    const fetchServices = async () => {
        setLoading(true);
        try {
            // Fetch service types
            const typesRes = await axios.get('/api/services/types');
            setServiceTypes(typesRes.data.data);

            // Fetch services in user's city
            const servicesRes = await axios.get(`/api/services?city=${userCity}&per_page=20`);
            setServices(servicesRes.data.data);

            // Fetch categorized services
            const categories = ['chefs', 'training', 'massage'];
            const catPromises = categories.map(cat => 
                axios.get(`/api/services?service_type=${cat}&per_page=8`)
            );
            const catResults = await Promise.all(catPromises);

            const catData = {};
            categories.forEach((cat, index) => {
                catData[cat] = catResults[index].data.data;
            });
            setCategorizedServices(catData);
        } catch (error) {
            console.error('Error fetching services:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="services-page">
            {/* Search Bar */}
            <div className="search-bar-wrapper bg-white border-bottom sticky-top">
                <div className="container py-3">
                    <SearchBar 
                        placeholder="Search services"
                        showServiceType={true}
                    />
                </div>
            </div>

            {/* Service Types Grid */}
            <section className="py-4 bg-light">
                <div className="container">
                    <h2 className="fw-bold mb-4">Services in {userCity}</h2>
                    <ServiceTypeGrid types={serviceTypes} city={userCity} />
                </div>
            </section>

            {/* More Services */}
            <section className="py-5">
                <div className="container">
                    <h3 className="h4 fw-semibold mb-4">More services in {userCity}</h3>

                    <div className="row g-3 g-lg-4">
                        {services.map(service => (
                            <div key={service.id} className="col-12 col-sm-6 col-lg-3">
                                <ServiceCard service={service} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* Discover Services */}
            <section className="py-5 bg-light">
                <div className="container">
                    <h2 className="fw-bold mb-5">Discover services on Airbnb</h2>

                    {Object.entries(categorizedServices).map(([category, items]) => (
                        <div key={category} className="mb-5">
                            <h3 className="h4 fw-semibold text-capitalize mb-4">
                                {category.replace('_', ' ')}
                            </h3>

                            <div className="horizontal-scroll">
                                <div className="d-flex gap-3">
                                    {items.map(service => (
                                        <div key={service.id} style={{minWidth: '300px'}}>
                                            <ServiceCard service={service} />
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};

export default ServicesPage;
